// src/MainDashboard.js
import React, { useState, useEffect } from 'react';
import './MainDashboard.css';

function MainDashboard() {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      setLoading(true);
      try {
        // Debugging: Log the API key
        console.log('API Key:', '01eef5d04f6f4fa1905203115242909'); //remove hard code later
        const apiKey = '01eef5d04f6f4fa1905203115242909';

        // Ensure the API key is present
        if (!apiKey) {
          throw new Error('API key is missing. Please check your .env file.');
        }

        const response = await fetch(
          `https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=London`
        );
        const data = await response.json();

        // Log the API response for debugging
        console.log('API Response:', data);

        if (data.error) {
          // API returned an error
          setError(data.error.message);
          setWeatherData(null);
        } else {
          // Successful API response
          setWeatherData(data);
          setError(null);
        }
      } catch (err) {
        // Network or other errors
        console.error('Fetch error:', err);
        setError(err.message);
        setWeatherData(null);
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  const getOutfitRecommendation = () => {
    if (!weatherData) return '';

    const temp = weatherData.current.temp_f;
    const condition = weatherData.current.condition.text.toLowerCase();

    let recommendation = '';

    // Simplified recommendation logic based on temperature
    if (temp <= 32) {
      recommendation = 'It\'s freezing! Wear a heavy coat, scarf, and gloves.';
    } else if (temp > 32 && temp <= 50) {
      recommendation = 'It\'s cold outside. Wear a coat and warm clothes.';
    } else if (temp > 50 && temp <= 68) {
      recommendation = 'It\'s cool. A light jacket would be good.';
    } else if (temp > 68 && temp <= 86) {
      recommendation = 'It\'s warm. Light clothing is recommended.';
    } else {
      recommendation = 'It\'s hot! Stay cool with shorts and a t-shirt.';
    }

    return recommendation;
  };

  return (
    <div className="dashboard">
      <h1>What Should I Wear Today?</h1>
      {loading && <p>Loading weather data...</p>}
      {error && <p>Error fetching data: {error}</p>}
      {weatherData && (
        <>
          <div className="weather-info">
            <p>
              <strong>Location:</strong> {weatherData.location.name}, {weatherData.location.country}
            </p>
            <p>
              <strong>Temperature:</strong> {weatherData.current.temp_f}Â°F
            </p>
            <p>
              <strong>Condition:</strong> {weatherData.current.condition.text}
            </p>
          </div>
          <div className="avatar">
            {/* Placeholder for the avatar image */}
            <img src="path_to_your_pixel_avatar.png" alt="Avatar" />
          </div>
          <div className="outfit-recommendation">
            <p><strong>Outfit Recommendation:</strong></p>
            <p>{getOutfitRecommendation()}</p>
          </div>
        </>
      )}
    </div>
  );
}

export default MainDashboard;
