// src/MainDashboard.js
import React, { useState, useEffect, useContext } from 'react';
import Avatar from './Avatar';
import UserContext from './UserContext';

function MainDashboard() {
  const { userPreferences } = useContext(UserContext);
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      setLoading(true);
      try {
        console.log('API Key:', process.env.REACT_APP_WEATHER_API_KEY); // Debug API Key
        const response = await fetch(
          `https://api.weatherapi.com/v1/current.json?key=${process.env.REACT_APP_WEATHER_API_KEY}&q=London`
        );
        const data = await response.json();

        console.log('API Response:', data); // Inspect API response

        if (data.error) {
          setError(data.error.message);
        } else {
          setWeatherData(data);
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  const getOutfitRecommendation = () => {
    if (!weatherData) return '';

    const { gender, style } = userPreferences;
    const temp = weatherData.current.temp_c;
    const condition = weatherData.current.condition.text.toLowerCase();

    let recommendation = '';

    // Simplified recommendation logic
    if (temp <= 0) {
      recommendation = 'Wear a heavy coat, scarf, and gloves.';
    } else if (temp > 0 && temp <= 10) {
      recommendation = 'Wear a coat and warm clothes.';
    } else if (temp > 10 && temp <= 20) {
      recommendation = 'A light jacket would be good.';
    } else if (temp > 20 && temp <= 30) {
      recommendation = 'Light clothing is recommended.';
    } else {
      recommendation = 'Stay cool with shorts and a t-shirt.';
    }

    return `Based on your ${style} ${gender} style: ${recommendation}`;
  };

  return (
    <div>
      <h1>Main Dashboard</h1>
      {loading && <p>Loading weather data...</p>}
      {error && <p>Error fetching data: {error}</p>}
      {weatherData && (
        <div>
          <p>
            Location: {weatherData.location.name}, {weatherData.location.country}
          </p>
          <p>Temperature: {weatherData.current.temp_c}Â°C</p>
          <p>Condition: {weatherData.current.condition.text}</p>
          <p>Outfit Recommendation: {getOutfitRecommendation()}</p>
        </div>
      )}
      <Avatar />
    </div>
  );
}

export default MainDashboard;
