// src/MainDashboard.js
import React, { useState, useEffect } from 'react';
import Avatar from './Avatar';
import './MainDashboard.css';

function MainDashboard() {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [manualLocation, setManualLocation] = useState('');

  useEffect(() => {
    const fetchWeather = async (latitude, longitude) => {
      setLoading(true);
      try {
        const apiKey = 'your_actual_api_key_here'; // Replace with your API key
        console.log('API Key:', apiKey);

        const locationQuery = `${latitude},${longitude}`;

        const response = await fetch(
          `https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${locationQuery}`
        );
        const data = await response.json();

        console.log('API Response:', data);

        if (data.error) {
          setError(data.error.message);
          setWeatherData(null);
        } else {
          setWeatherData(data);
          setError(null);
        }
      } catch (err) {
        console.error('Fetch error:', err);
        setError(err.message);
        setWeatherData(null);
      } finally {
        setLoading(false);
      }
    };

    const getUserLocation = () => {
      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const { latitude, longitude } = position.coords;
            console.log('User coordinates:', latitude, longitude);
            fetchWeather(latitude, longitude);
          },
          (error) => {
            console.error('Geolocation error:', error);
            switch (error.code) {
              case error.PERMISSION_DENIED:
                setError('Permission denied. Please allow location access.');
                break;
              case error.POSITION_UNAVAILABLE:
                setError('Location information is unavailable.');
                break;
              case error.TIMEOUT:
                setError('The request to get your location timed out.');
                break;
              default:
                setError('An unknown error occurred while retrieving your location.');
                break;
            }
            setLoading(false);
          }
        );
      } else {
        console.error('Geolocation not supported');
        setError('Geolocation is not supported by your browser.');
        setLoading(false);
      }
    };

    getUserLocation();
  }, []);

  const fetchWeatherByManualLocation = async () => {
    if (!manualLocation) {
      setError('Please enter a location.');
      return;
    }

    setLoading(true);
    try {
      const apiKey = '01eef5d04f6f4fa1905203115242909'; // Replace with your API key

      const response = await fetch(
        `https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${manualLocation}`
      );
      const data = await response.json();

      console.log('API Response:', data);

      if (data.error) {
        setError(data.error.message);
        setWeatherData(null);
      } else {
        setWeatherData(data);
        setError(null);
      }
    } catch (err) {
      console.error('Fetch error:', err);
      setError(err.message);
      setWeatherData(null);
    } finally {
      setLoading(false);
    }
  };

  const getOutfitRecommendation = () => {
    if (!weatherData) return '';

    const temp = weatherData.current.temp_f;
    const condition = weatherData.current.condition.text.toLowerCase();

    let recommendation = '';

    if (temp <= 32) {
      recommendation = 'It\'s freezing! Wear a heavy coat, scarf, and gloves.';
    } else if (temp > 32 && temp <= 50) {
      recommendation = 'It\'s cold outside. Wear a coat and warm clothes.';
    } else if (temp > 50 && temp <= 68) {
      recommendation = 'It\'s cool. A light jacket would be good.';
    } else if (temp > 68 && temp <= 86) {
      recommendation = 'It\'s warm. Light clothing is recommended.';
    } else {
      recommendation = 'It\'s hot! Stay cool with shorts and a t-shirt.';
    }

    return recommendation;
  };

  const recommendation = getOutfitRecommendation();

  return (
    <div className="dashboard">
      <h1>What Should I Wear Today?</h1>
      {loading && !error && <p>Requesting your location...</p>}
      {error && !weatherData && (
        <div>
          <p>Error fetching data: {error}</p>
          <p>You can enter your location manually:</p>
          <input
            type="text"
            value={manualLocation}
            onChange={(e) => setManualLocation(e.target.value)}
            placeholder="Enter city or ZIP code"
          />
          <button onClick={() => fetchWeatherByManualLocation()}>Submit</button>
        </div>
      )}
      {weatherData && (
        <>
          <div className="weather-info">
            <p>
              <strong>Location:</strong> {weatherData.location.name}, {weatherData.location.country}
            </p>
            <p>
              <strong>Temperature:</strong> {weatherData.current.temp_f}Â°F
            </p>
            <p>
              <strong>Condition:</strong> {weatherData.current.condition.text}
            </p>
            <img
              src={weatherData.current.condition.icon}
              alt={weatherData.current.condition.text}
              className="weather-icon"
            />
          </div>
          <Avatar weatherData={weatherData} />
          <div className="outfit-recommendation">
            <p><strong>Outfit Recommendation:</strong></p>
            <p>{recommendation}</p>
          </div>
        </>
      )}
    </div>
  );
}

export default MainDashboard;
